INSERT INTO `virsh` (`title`, `virsh`, `enabled`, `illustration`, `illustration_enabled`, `youtube`, `youtube_enabled`) VALUES
('Зимова ніч у місті', 'Сніг лягає тихо-тихо на асфальт,\nЩо вранці стане чорним від машин.\nМісяць світить крізь смог і сажу,\nВітер грає між бетонних стін.\n\nДитина мріє про санчата,\nА мама думає про рахунки.\nЗима у місті - то не казка,\nА просто холод і черги.\n\nАле все ж таки є магія\nУ тому, як сніжинки падають.\nНавіть коли вони чорніють,\nВони спочатку білі, знаєш.', 1, '', 1, '', 1),
('Весняний ранок програміста', 'Сонце встало, а я ще не спав,\nКод писав усю ніч до світання.\nПташки співають за вікном - дратує,\nЯк же можна працювати при такому гаманні?\n\nВесна приносить радість? Може комусь.\nМені приносить алергію та сонце в монітор.\nЗемля прокинулась від сну,\nА я б заснув, але дедлайн не жарт, батько.\n\nКава холодна, очі червоні,\nВесняний ранок - це знову баг.\nАле код нарешті працює,\nІ це, друзі, найкращий із снідань.', 1, '', 1, '', 1),
('Літні мрії офісного працівника', 'Тепле літо, душний офіс,\nКондиціонер знову зламався.\nКвіти всюди? Ні, бетон та скло,\nА відпустка вже закінчилась.\n\nМетелики танцюють? Де ви їх бачили?\nТут тільки муха б\'ється об скло.\nДіти сміються у садку? Ага,\nА я рахую дні до зарплатні, знаєш.\n\nМрію про море, про пісок,\nПро коктейль з парасолькою маленькою.\nАле реальність - це звіти та зустрічі,\nТа кава з автомату несмачна.\n\nЛіто - пора надій та мрій,\nЯкі розбиваються об буденність.\nАле ще є два місяці,\nМожливо, щось зміниться... або ні.', 1, '', 1, '', 1),
('Осінній вечір філософа', 'Листя жовте опадає - який банальний образ,\nВитертий мільйонами поетів до дір.\nВітер холод приносить? Ще б пак,\nОсінь робить те, що вона завжди робила.\n\nЯ сиджу з філософською книжкою,\nРозмірковую про сенс буття.\nА листя собі падає, йому байдуже,\nЩо я тут філософствую про життя.\n\nПрирода засинає? Це метафора.\nНасправді починається підготовка до весни.\nАле поети люблять драматизувати,\nБо іначе вірші виходять нудні, прості.\n\nОсінь - час підвести підсумки,\nПодумати, що зроблено за рік.\nІ зрозуміти, що листя мудріше:\nВоно просто падає, не думаючи навіщо.', 1, '', 1, '', 1),
('Зоряна ніч астронома', 'Зорі яскраві мерехтять - не зовсім,\nЦе атмосферна турбулентність, друже.\nНіч темна? Та світлове забруднення\nРобить її сірою, майже не чорною.\n\nМісяць світить з висоти - це правда,\nАле це лише відбите світло сонця.\nПрирода спить безтурботна?\nХіба що ви не чули нічних тварин.\n\nЯ дивлюсь у телескоп годинами,\nШукаю планети, туманності, галактики.\nА люди дивляться на зірки і мріють,\nНе знаючи, що бачать світло з минулого.\n\nДеякі зорі вже мертві,\nАле їхнє світло все ще в дорозі.\nЗоряна ніч - це подорож у часі,\nАле романтики про це не думають, звісно.', 1, '', 1, '', 1),
('Ранкова роса скептика', 'Роса на траві сріблиться - красиво,\nАле це просто конденсація води.\nРанок новий прокидається? Знову?\nОт сюрприз, сонце знову встало.\n\nЖиття знову починається - ні, воно продовжується,\nБо воно ніколи не зупинялось.\nСонце з\'являється над обрієм,\nЯк і вчора, позавчора, і мільярд років тому.\n\nА я прокидаюсь з головним болем,\nДивлюсь на цю росу срібну.\nІ думаю - так, це гарно,\nАле кава зараз важливіша.\n\nРанок - це не новий початок,\nЦе просто продовження того самого.\nАле роса справді красива,\nЗаки її випарить сонце через годину.', 1, '', 1, '', 1);
