INSERT INTO `virsh` (`title`, `virsh`, `enabled`, `illustration`, `illustration_enabled`, `youtube`, `youtube_enabled`) VALUES
('Зимова ніч', 'Сніг лягає тихо-тихо,\nМісяць світить у вікно.\nВітер грає мелодії\nПід зимовим полотном.', 1, '', 1, '', 1),
('Весняний ранок', 'Сонце встало над горою,\nПташки співають у саду.\nЗемля прокинулась від сну,\nВесна приносить радість нову.', 1, '', 1, '', 1),
('Літні мрії', 'Тепле літо, ясні дні,\nКвіти всюди розцвіли.\nМетелики танцюють в повітрі,\nА діти сміються у садку.', 1, '', 1, '', 1),
('Осінній вечір', 'Листя жовте опадає,\nВітер холод приносить.\nОсінь тихо наступає,\nПрироду в сон заносить.', 1, '', 1, '', 1),
('Зоряна ніч', 'Зорі яскраві мерехтять,\nНіч темна та спокійна.\nМісяць світить з висоти,\nПрирода спить безтурботна.', 1, '', 1, '', 1),
('Ранкова роса', 'Роса на траві сріблиться,\nРанок новий прокидається.\nСонце над обрієм з\'являється,\nЖиття знову починається.', 1, '', 1, '', 1),
('Гірський потік', 'Потік гірський швидко біжить,\nМіж каменями грає.\nВода чиста і холодна,\nПісню свою співає.', 1, '', 1, '', 1),
('Польовий квіт', 'Маленький квітик у полі,\nВітром гойдається.\nКрасою простою сяє,\nСонцю усміхається.', 1, '', 1, '', 1),
('Дощова пісня', 'Дощ стукає по вікнах,\nХмари сірі пливуть.\nКраплі падають ритмічно,\nПісню дощову плетуть.', 1, '', 1, '', 1),
('Морський бриз', 'Море синє хвилюється,\nБриз солоний віє.\nЧайки білі кричать,\nВода берег цілує.', 1, '', 1, '', 1),
('Лісова казка', 'У глибокому лісі темному,\nДе сонце ледь проникає,\nКазка давня живе,\nСвоїм чаром надихає.', 1, '', 1, '', 1),
('Нічний спокій', 'Тиша ночі обгортає,\nЗірки тихо світять.\nВсе навколо засинає,\nДуша може відпочити.', 1, '', 1, '', 1),
('Весняний дощ', 'Теплий дощ весною іде,\nЗемлю поливає.\nТрава зелена проростає,\nПрирода оживає.', 1, '', 1, '', 1),
('Сонячний день', 'Яскраве сонце сяє,\nТепло всім роздає.\nНастрій піднімає,\nРадість в серці наповняє.', 1, '', 1, '', 1),
('Місячне сяйво', 'Місяць повний у небі,\nСріблом все заливає.\nТіні довгі лягають,\nНіч магію створює.', 1, '', 1, '', 1),
('Гірська вершина', 'На вершині гори високої,\nДе орли гніздяться.\nВідкриваються краєвиди,\nДуша вільною стає.', 1, '', 1, '', 1),
('Річка життя', 'Річка тече невпинно,\nНе зупиняється ніколи.\nТак і життя наше пливе,\nВ майбутнє несе нас всіх.', 1, '', 1, '', 1),
('Садовий кут', 'У тихому куточку саду,\nДе квіти розцвітають,\nМетелики танцюють,\nБджоли мед збирають.', 1, '', 1, '', 1),
('Вечірня зоря', 'Перша зірка з\'явилася,\nВечір настає.\nДень минає поволі,\nНіч нас зустрічає.', 1, '', 1, '', 1),
('Ранкова тиша', 'Ранок тихий і спокійний,\nРоса на листі блищить.\nСвіт ще спить глибоко,\nТільки птах один кричить.', 1, '', 1, '', 1),
('Зимовий ліс', 'Ліс зимовий у снігу,\nДерева білі стоять.\nТиша панує всюди,\nТільки вітер шумить.', 1, '', 1, '', 1),
('Весняні квіти', 'Підсніжники прокинулись,\nІз снігу пробиваються.\nВесна вже йде назустріч,\nКвіти усміхаються.', 1, '', 1, '', 1),
('Літня спека', 'Сонце пече нещадно,\nПовітря гаряче.\nВсі шукають прохолоди,\nДень літній палючий.', 1, '', 1, '', 1),
('Осінній дощ', 'Дощ осінній моросить,\nЛистя мокре лежить.\nХолод в повітрі відчувається,\nОсінь вже господарює.', 1, '', 1, '', 1),
('Зоряне небо', 'Небо вночі чисте,\nЗірки ясно світять.\nВсесвіт безкрайній,\nТаємниці свої ховає.', 1, '', 1, '', 1),
('Ранкова свіжість', 'Повітря ранкове свіже,\nПахне травою і росою.\nНовий день починається,\nСвітло наповнюється душа.', 1, '', 1, '', 1),
('Гірське озеро', 'Озеро гірське чисте,\nВода прозора як скло.\nГори відбиваються в воді,\nКрасива картина.', 1, '', 1, '', 1),
('Польова дорога', 'Дорога польова звивається,\nВеде в далечінь.\nКуди приведе - невідомо,\nАле йти треба вперед.', 1, '', 1, '', 1),
('Дощові краплі', 'Краплі дощу на склі,\nПовзуть вниз поволі.\nРитм спокійний і тихий,\nЗаспокоює душу.', 1, '', 1, '', 1),
('Морські хвилі', 'Хвилі моря котяться,\nОдна за одною.\nБіла піна спінюється,\nПісню вічну співає.', 1, '', 1, '', 1),
('Лісова стежка', 'Стежка лісом петляє,\nМіж деревами іде.\nКуди приведе - побачимо,\nПригоди нас чекають.', 1, '', 1, '', 1),
('Нічна прохолода', 'Ніч принесла прохолоду,\nПісля спекотного дня.\nПовітря свіже і чисте,\nДихається легко.', 1, '', 1, '', 1),
('Весняний вітер', 'Вітер весняний теплий,\nПахне квітами.\nПриносить радісні вісті,\nПро те, що весна йде.', 1, '', 1, '', 1),
('Сонячний промінь', 'Промінь сонця крізь хмари,\nПробивається на землю.\nОсвітлює все навколо,\nТепло і світло дарує.', 1, '', 1, '', 1),
('Місячна доріжка', 'На воді місячна доріжка,\nСріблом мерехтить.\nМанить за собою,\nВ далечінь таємничу.', 1, '', 1, '', 1),
('Гірський вітер', 'Вітер гірський сильний,\nСвистить між скелями.\nПриносить прохолоду,\nСвободу дарує.', 1, '', 1, '', 1),
('Річкова течія', 'Річка несе свої води,\nДо моря прямує.\nНічого не зупинить її,\nВона свій шлях знає.', 1, '', 1, '', 1),
('Садова алея', 'Алея в саду зелена,\nДерева тінь створюють.\nТихо і спокійно,\nДумки приходять світлі.', 1, '', 1, '', 1),
('Вечірні барви', 'Захід сонця барвистий,\nНебо червоно-помаранчеве.\nКрасиво і яскраво,\nЯк картина справжня.', 1, '', 1, '', 1),
('Ранковий туман', 'Туман вранці стелиться,\nВсе вкриває білим.\nТаємничо і красиво,\nНемов казка справжня.', 1, '', 1, '', 1),
('Зимові сутінки', 'Сутінки зимові ранні,\nДень короткий дуже.\nСніг скрипить під ногами,\nХолод проймає.', 1, '', 1, '', 1),
('Весняна радість', 'Весна приносить радість,\nВсе оживає знову.\nПташки співають пісні,\nКвіти розпускаються.', 1, '', 1, '', 1),
('Літній полудень', 'Полудень літній спекотний,\nСонце у зеніті.\nВсі шукають тіні,\nВід спеки ховаються.', 1, '', 1, '', 1),
('Осінній вітер', 'Вітер осінній холодний,\nЛистя розганяє.\nЗима вже близько,\nОсінь завершується.', 1, '', 1, '', 1),
('Зоряний дощ', 'Зірки падають з неба,\nНемов дощ справжній.\nБажання загадати можна,\nВони здійсняться певно.', 1, '', 1, '', 1),
('Ранкова пісня', 'Птахи співають вранці,\nВітають новий день.\nЇх пісня радісна лунає,\nДушу наповняє світлом.', 1, '', 1, '', 1),
('Гірський струмок', 'Струмок гірський журчить,\nМіж камінням біжить.\nВода чиста і холодна,\nДзвенить як дзвіночок.', 1, '', 1, '', 1),
('Польові трави', 'Трави в полі коливаються,\nВітром гойдаються.\nЗелений килим безкрайній,\nДо горизонту тягнеться.', 1, '', 1, '', 1),
('Дощова веселка', 'Після дощу веселка,\nСім кольорів яскравих.\nАрка небесна красива,\nРадість і надію дарує.', 1, '', 1, '', 1),
('Морський захід', 'Сонце сідає в море,\nВода червона стає.\nКрасивий вид прощальний,\nДень завершується тихо.', 1, '', 1, '', 1),
('Лісовий шум', 'Ліс шумить листям,\nВітер гуляє між гілок.\nПрирода співає пісню,\nСвою власну мелодію.', 1, '', 1, '', 1),
('Нічна казка', 'Ніч казкову атмосферу,\nНавколо створює.\nВсе таємниче стає,\nФантазія грає.', 1, '', 1, '', 1),
('Весняна краса', 'Краса весняна всюди,\nКвіти, зелень, тепло.\nПрирода прокидається,\nЖиття нове починається.', 1, '', 1, '', 1),
('Сонячний зайчик', 'Зайчик сонячний грає,\nПо стінах танцює.\nРадість приносить дитячу,\nУсмішки викликає.', 1, '', 1, '', 1),
('Місячна казка', 'Місяць казку розповідає,\nСвоїм сяйвом тихим.\nХто уважно слухає,\nДізнається таємниці.', 1, '', 1, '', 1),
('Гірська тиша', 'Тиша в горах глибока,\nТільки вітер іноді.\nСпокій і мир панують,\nДуша відпочиває.', 1, '', 1, '', 1),
('Річковий берег', 'На березі річки тихій,\nДе верби схилились.\nМожна сидіти годинами,\nДумками відлітати.', 1, '', 1, '', 1),
('Садовий світанок', 'Світанок у саду ясний,\nПташки пробуджуються.\nРоса на квітах блищить,\nНовий день розпочинається.', 1, '', 1, '', 1),
('Вечірня зірка', 'Зірка вечірня яскрава,\nПерша на небі.\nВітає настання ночі,\nСвітить всім дорогу.', 1, '', 1, '', 1),
('Ранкова прохолода', 'Ранок прохолодний свіжий,\nПовітря чисте.\nДихається легко,\nСили нові дає.', 1, '', 1, '', 1),
('Зимова казка', 'Зима казку створила,\nВсе білим вкрила.\nСніг блищить на сонці,\nМороз малює візерунки.', 1, '', 1, '', 1),
('Весняні промені', 'Промені весняні теплі,\nСніг розтоплюють.\nЗемля прокидається,\nЖиття знову з\'являється.', 1, '', 1, '', 1),
('Літня ніч', 'Ніч літня коротка,\nТепла і зоряна.\nСпати не хочеться,\nКрасою милуєшся.', 1, '', 1, '', 1),
('Осінні барви', 'Осінь фарбами грається,\nЛистя жовтить, червонить.\nПрирода в барвах яскравих,\nПрощається з літом.', 1, '', 1, '', 1),
('Зоряний шлях', 'Шлях зоряний у небі,\nМлявий туман світиться.\nВселенна величезна,\nБезкрайня і прекрасна.', 1, '', 1, '', 1),
('Ранковий світ', 'Світ ранком прокидається,\nНовим життям наповнюється.\nВсе починається знову,\nНадія оживає.', 1, '', 1, '', 1),
('Гірський краєвид', 'Краєвид гірський величний,\nДух захоплює.\nВисокі вершини білі,\nНебо торкаються.', 1, '', 1, '', 1),
('Польовий вітер', 'Вітер полем гуляє,\nТрави коливає.\nСвободу приносить,\nДушу звільняє.', 1, '', 1, '', 1),
('Дощова симфонія', 'Дощ грає симфонію,\nКраплями по даху.\nРитм його заспокійливий,\nСон міцний дарує.', 1, '', 1, '', 1),
('Морський простір', 'Море безкрайнє синє,\nДо горизонту тягнеться.\nХвилі грають і танцюють,\nПісню вічну співають.', 1, '', 1, '', 1),
('Лісова гармонія', 'Ліс живе своїм життям,\nГармонія в ньому.\nВсе на своїх місцях,\nПрирода мудра.', 1, '', 1, '', 1),
('Нічне небо', 'Небо вночі темне,\nЗірками всіяне.\nБезкрайнє і таємниче,\nВсесвіт відкриває.', 1, '', 1, '', 1),
('Весняна надія', 'Весна надію приносить,\nНа краще майбутнє.\nВсе оновлюється знову,\nЖиття продовжується.', 1, '', 1, '', 1),
('Сонячне тепло', 'Тепло сонячне м\'яке,\nОбгортає ніжно.\nДушу зігріває,\nРадість дарує.', 1, '', 1, '', 1),
('Місячний спокій', 'Спокій місячної ночі,\nТиха і безтурботна.\nВсе засинає поволі,\nМир настає всюди.', 1, '', 1, '', 1),
('Гірська свіжість', 'Свіжість гірського повітря,\nЧисте і прозоре.\nДихається легко,\nСили додає.', 1, '', 1, '', 1),
('Річкова мелодія', 'Річка співає мелодію,\nСвою власну пісню.\nТече невпинно вперед,\nДо моря прямує.', 1, '', 1, '', 1),
('Садовий спокій', 'Спокій садовий тихий,\nДе квіти ростуть.\nМожна відпочити,\nДушею відновитись.', 1, '', 1, '', 1),
('Вечірній світ', 'Світ вечірній м\'який,\nБарви приглушені.\nТиша поступово,\nНастає всюди.', 1, '', 1, '', 1),
('Ранкова енергія', 'Енергія ранку нова,\nСили дає свіжі.\nДень попереду,\nМожливості безкінечні.', 1, '', 1, '', 1),
('Зимова чистота', 'Чистота зимова біла,\nСніг усе вкриває.\nСвіт немов народжується,\nЗнову чистим стає.', 1, '', 1, '', 1),
('Весняне диво', 'Диво весняне щороку,\nПовторюється знову.\nПрирода пробуджується,\nЖиття перемагає.', 1, '', 1, '', 1),
('Літня повнота', 'Повнота літа щедра,\nВсього вдосталь.\nТепло, світло, краса,\nЖиття б\'є ключем.', 1, '', 1, '', 1),
('Осіння мудрість', 'Мудрість осені спокійна,\nЧас підведення підсумків.\nПрирода готується до сну,\nЦикл завершується.', 1, '', 1, '', 1),
('Зоряна вічність', 'Вічність зірок незрозуміла,\nСвітять мільйони років.\nЇхнє світло до нас йде,\nКрізь простір і час.', 1, '', 1, '', 1),
('Ранкова гармонія', 'Гармонія ранку досконала,\nВсе на своїх місцях.\nПрирода прокидається,\nНовий день починається.', 1, '', 1, '', 1),
('Гірський дух', 'Дух гір величний,\nСильний і вільний.\nВисоко над суєтою,\nБлизько до неба.', 1, '', 1, '', 1),
('Польова свобода', 'Свобода польова безмежна,\nПростір відкритий.\nМожна бігти куди хочеш,\nНіхто не зупинить.', 1, '', 1, '', 1),
('Дощова музика', 'Музика дощу ніжна,\nГрає на листі.\nКожна крапля - нота,\nСимфонія природи.', 1, '', 1, '', 1),
('Морська безкрайність', 'Безкрайність моря манить,\nДалекі обрії.\nХочеться пливти туди,\nДе небо з водою зустрічаються.', 1, '', 1, '', 1),
('Лісова таємниця', 'Таємниця лісу давня,\nЗберігається століттями.\nХто її знає?\nТільки старі дерева.', 1, '', 1, '', 1),
('Нічна краса', 'Краса ночі особлива,\nНе така як денна.\nТаємнича і притягальна,\nМагічна і незвична.', 1, '', 1, '', 1),
('Весняне пробудження', 'Пробудження весняне,\nПовільне але вірне.\nПрирода розкривається,\nЯк квітка на світанку.', 1, '', 1, '', 1),
('Сонячна радість', 'Радість сонця щира,\nТепло і світло дарує.\nВсім однаково світить,\nНікого не минає.', 1, '', 1, '', 1),
('Місячна магія', 'Магія місяця стара,\nВіками відома.\nВпливає на води,\nНа серця і душі.', 1, '', 1, '', 1);
