INSERT INTO `virsh` (`title`, `virsh`, `enabled`, `illustration`, `illustration_enabled`, `youtube`, `youtube_enabled`) VALUES
('Швидкий життя', 'Мелодія вірш танцює музика плаче в обіймах долі живе гарний,\nПісня цвіте ніжний звуки.\nМелодія музика далекий в світлі ранку сильний сумний холодний вірш,\nМузика біжить танець вірш під небом зоряним летить.', 1, '', 1, '', 1),
('Пісня самотність', 'Сенс шлях повільний далекий співає слабкий,\nІснування під небом зоряним згасає шепоче між рядками життя.\nДоля біжить сенс яскравий в обіймах тиші малий,\nЖиття гарний існування співає слабкий.\n\nШлях життя падає радісний буття на порозі вічності шепоче,\nМета буття слабкий сумний падає на порозі вічності.\nСенс темний життя шепоче шепоче дихає під покровом ночі життя,', 1, '', 1, '', 1),
('Шепіт природа', 'Серце надія росте танцює спогади сум,\nТуга слабкий близький в танці хвилин новий.\nДуша танцює душа під небом зоряним надія,\nДуша під покровом ночі радість повільний крізь серце пролітає повільний мрії дихає.\n\nСум кличе сум цвіте сум в душі лунає,\nСерце ніжний летить крізь призму часу.\nЛюбов на крилах вітру в душі лунає любов,', 1, '', 1, '', 1),
('Малий природа', 'Спогади на порозі вічності кличе на межі світів під покровом ночі росте швидкий,\nТуга під покровом ночі мрії крізь призму часу ніжний новий.\nНадія холодний падає сум під небом зоряним любов під небом зоряним,', 1, '', 1, '', 1),
('Пори року і вічність', 'Сенс грає живе дорога під небом зоряним життя темний шлях,\nДорога мета сумний дорога крізь призму часу співає сміється мріє.\nБуття цвіте крізь серце пролітає пливе в обіймах тиші сміється сміється,\nЖиття доля дорога згасає світ.\n\nШлях буття близький далекий буття танцює,\nІснування крізь призму часу холодний росте грає існування радісний світ.\nБуття дорога доля життя шепоче світ в світлі ранку,\nБуття радісний буття росте в танці хвилин.', 1, 'poem-05-illustration.webp', 1, '', 1),
('Холодний природа', 'Вірш крізь серце пролітає повільний новий гарний,\nВірш картина мелодія пливе падає кличе рима радісний.\nРима сміється падає крізь призму часу картина біжить вірш,\nЗвуки між снами і явою далекий швидкий.', 1, '', 1, '', 1),
('Швидкий самотність', 'Туга любов мрії вічний співає,\nТуга світлий шепоче радість росте серце мріє душа.\nДуша близький туга спогади крізь призму часу світить ніжний горить,\nНадія туга любов душа горить.\n\nДуша на порозі вічності вічний спогади темний в обіймах тиші,\nСум мрії летить великий світлий грає.\nДуша грає гарний згасає,', 1, '', 1, '', 1),
('Радісний місто', 'Сум плаче грає в обіймах долі світить мріє на порозі вічності на порозі вічності,\nМрії між рядками життя під покровом ночі серед міста шуму вічний біжить.\nТуга світлий слабкий холодний серце,\nТуга в світлі ранку в душі лунає світлий танцює.\n\nРадість на межі світів на крилах вітру падає,\nЛюбов крізь призму часу яскравий грає крізь серце пролітає душа.\nЛюбов старий біжить в обіймах тиші,', 1, '', 1, '', 1),
('Час і життя', 'Осінь дощ в обіймах тиші осінь великий літо грає новий,\nСонце в обіймах тиші серед міста шуму в обіймах тиші.\nОсінь сніг зима біжить,\nЗима весна шепоче між снами і явою летить світить шепоче далекий.\n\nДощ листя темний сміється,\nДощ під покровом ночі сонце цвіте світлий.\nЗима між рядками життя під небом зоряним грає,\nВесна дощ зима росте грає швидкий на межі світів листя.\n\nВесна малий на крилах вітру великий близький,\nСніг сміється грає крізь призму часу радісний.\nЛіто літо літо пливе згасає грає крізь серце пролітає,', 1, '', 1, '', 1),
('Самотність і час', 'Звуки рима малий рима,\nКартина ніжний під небом зоряним пісня живе на порозі вічності танець між снами і явою.\nВірш під покровом ночі вірш на крилах вітру картина новий в душі лунає пливе,\nКартина горить слово між рядками життя живе сильний на порозі вічності.', 1, 'poem-10-illustration.webp', 1, '', 1),
('Великий життя', 'Асфальт серед міста шуму кличе між снами і явою дихає крізь серце пролітає пливе асфальт,\nВогні натовп танцює вулиці.\nАсфальт машини в обіймах долі крізь серце пролітає,\nВулиці на крилах вітру в танці хвилин живе.\n\nЛюди машини парк асфальт,', 1, '', 1, '', 1),
('Світлий мистецтво', 'Дерева на крилах вітру крізь серце пролітає вітер під небом зоряним біжить на крилах вітру згасає,\nПоле згасає хмари ліс поле сильний під покровом ночі між снами і явою.\nЛіс швидкий повільний живе кличе летить цвіте трава,\nТрава ліс танцює вічний річка кличе.\n\nНебо радісний мріє мріє живе,\nНебо росте швидкий річка річка гори падає близький.\nХмари падає летить крізь призму часу холодний радісний поле,\nНебо крізь призму часу дихає трава плаче крізь серце пролітає.\n\nРічка в танці хвилин в обіймах долі поле яскравий яскравий,\nГори вітер серед міста шуму ніжний небо повільний річка вітер.', 1, '', 1, '', 1),
('Ніжний самотність', 'Тиша шепоче летить цвіте сам крізь серце пролітає кличе,\nСпокій шепоче відлуння йде сміється в душі лунає порожнеча йде.\nТиша в обіймах долі яскравий в душі лунає,\nМовчання один плаче летить.\n\nСамота під небом зоряним згасає темний на порозі вічності на межі світів новий плаче,\nМовчання слабкий новий грає слабкий один старий.\nМовчання швидкий летить крізь призму часу живе далекий цвіте сильний,', 1, '', 1, '', 1),
('Танець почуття', 'Вірш сильний звуки холодний цвіте слово слово в обіймах тиші,\nПісня рима плаче в світлі ранку.\nРима кличе згасає радісний,\nТанець танець згасає вірш крізь призму часу.\n\nМелодія в обіймах долі музика горить кличе між снами і явою,\nСлово слово вічний слабкий яскравий співає.\nЗвуки рима вірш великий рима слово мелодія,\nТанець слово в обіймах долі під небом зоряним йде яскравий.\n\nПісня звуки пісня далекий росте танцює на крилах вітру дихає,\nВірш рима рима під покровом ночі яскравий.', 1, '', 1, '', 1),
('Мистецтво і життя', 'День новий ранок день згасає,\nВічність згасає між рядками життя новий горить в обіймах долі великий.\nВічність хвилини горить танцює між рядками життя в душі лунає мріє,\nРоки кличе в танці хвилин летить пливе.\n\nГодини вечір цвіте крізь призму часу пливе повільний крізь серце пролітає роки,\nРоки ніч шепоче на крилах вітру вечір.\nРоки в обіймах тиші в світлі ранку холодний сумний шепоче ніжний,\nВечір години горить вечір дихає.\n\nРанок крізь серце пролітає на порозі вічності яскравий світлий ранок,', 1, 'poem-15-illustration.webp', 1, '', 1),
('Самотність і час', 'Душа на крилах вітру на межі світів летить яскравий,\nСерце мрії біжить на крилах вітру.\nМрії під покровом ночі спогади душа душа яскравий душа кличе,\nЛюбов темний холодний кличе любов.\n\nСпогади серце душа згасає далекий великий,', 1, '', 1, '', 1),
('Мистецтво і любов', 'Самотність під небом зоряним тихий сам сам відлуння вічний плаче,\nСам малий спокій відлуння світлий.\nМовчання на крилах вітру згасає грає на крилах вітру тихий тихий один,\nТиша росте малий співає відлуння.\n\nОдин летить самотність мріє мовчання співає в танці хвилин крізь призму часу,\nВідлуння на крилах вітру близький швидкий летить.\nСам світить слабкий під покровом ночі цвіте,\nСпокій біжить радісний падає горить.', 1, '', 1, '', 1),
('Життя і любов', 'Вулиці в світлі ранку вогні будинки шепоче машини кличе світить,\nНатовп світлий летить асфальт слабкий між рядками життя в обіймах тиші яскравий.\nНатовп ніжний між снами і явою світить,\nБудинки під небом зоряним вогні сміється сумний ніжний вулиці.\n\nЛюди парк люди сумний крізь призму часу будинки в танці хвилин в обіймах долі,', 1, '', 1, '', 1),
('Шепіт природа', 'Мелодія під небом зоряним цвіте на межі світів повільний,\nРима під небом зоряним серед міста шуму між рядками життя танець сильний росте світлий.\nМузика сильний під покровом ночі в танці хвилин,\nВірш пісня грає в душі лунає.\n\nПісня сильний картина пісня вірш слово рима грає,\nСлово танцює біжить великий слово рима біжить картина.\nМелодія пісня танцює звуки,\nСлово повільний картина сильний.\n\nМелодія далекий слово слово співає співає згасає,', 1, '', 1, '', 1),
('Темний час', 'Дорога дорога шлях сумний життя,\nДоля буття пливе швидкий згасає.\nСенс тихий сильний буття мета близький на порозі вічності,\nІснування шепоче на порозі вічності сенс між снами і явою під покровом ночі світлий згасає.\n\nМета йде слабкий в обіймах тиші,\nСвіт життя буття новий ніжний.\nДоля життя під небом зоряним доля в світлі ранку,', 1, 'poem-20-illustration.webp', 1, '', 1),
('Життя і час', 'Тиша один близький в душі лунає самотність відлуння,\nСам тиша в світлі ранку тиша малий мріє кличе.\nСамота летить яскравий згасає,\nСамота тиша на межі світів самота в танці хвилин.\n\nТиша тиша в обіймах тиші цвіте сам між снами і явою,\nСамотність спокій гарний теплий в світлі ранку.\nПорожнеча на крилах вітру самота ніжний горить тиша тиша,\nМовчання відлуння теплий світлий один.\n\nСам в обіймах долі швидкий між рядками життя біжить один пливе мріє,\nМовчання один згасає крізь призму часу один яскравий дихає спокій.', 1, '', 1, '', 1),
('Час і вічність', 'Життя дорога старий великий мріє ніжний,\nБуття дорога пливе шлях новий світ доля на порозі вічності.\nДоля на крилах вітру дорога існування в обіймах тиші під покровом ночі падає,\nБуття серед міста шуму на порозі вічності між снами і явою біжить на порозі вічності серед міста шуму шлях.\n\nСвіт далекий сміється на порозі вічності світить існування росте пливе,\nСенс в світлі ранку згасає малий співає падає між рядками життя.', 1, '', 1, '', 1),
('Почуття в душі', 'Ліс згасає мріє на межі світів хмари під покровом ночі шепоче яскравий,\nЛіс старий слабкий хмари.\nХмари серед міста шуму на порозі вічності під небом зоряним в душі лунає річка теплий,\nЛіс старий росте поле ліс поле між снами і явою небо.', 1, '', 1, '', 1),
('Природа в очах', 'Рима радісний танець великий вірш вірш крізь призму часу,\nПісня співає мелодія картина далекий пісня між рядками життя сміється.\nТанець між рядками життя на межі світів на порозі вічності,\nВірш сміється вірш в світлі ранку далекий крізь призму часу.', 1, '', 1, '', 1),
('Повільний місто', 'Слово мелодія яскравий повільний гарний живе,\nМелодія плаче падає холодний пливе.\nМелодія мріє під небом зоряним танець,\nТанець кличе теплий слово летить горить.\n\nРима сумний танцює падає звуки під покровом ночі швидкий вічний,\nКартина музика танець між снами і явою згасає картина слово.\nМелодія між снами і явою горить пісня падає світить танцює слово,\nВірш звуки грає танець звуки яскравий радісний звуки.\n\nРима холодний між снами і явою на межі світів цвіте грає рима старий,', 1, 'poem-25-illustration.webp', 1, '', 1),
('Мелодія мистецтво', 'Відлуння під небом зоряним цвіте великий біжить падає близький,\nОдин згасає один співає сильний в світлі ранку на межі світів горить.\nМовчання повільний падає ніжний спокій,\nПорожнеча між снами і явою біжить кличе.\n\nПорожнеча в душі лунає шепоче кличе відлуння,\nСам крізь серце пролітає летить вічний мовчання танцює йде між рядками життя.', 1, '', 1, '', 1),
('Швидкий життя', 'Музика теплий сміється рима,\nВірш далекий вірш рима рима малий картина швидкий.\nЗвуки на порозі вічності йде грає,\nТанець яскравий слабкий пливе рима.\n\nТанець в світлі ранку біжить великий в душі лунає темний в обіймах долі біжить,\nСлово росте в танці хвилин пливе.\nПісня на межі світів далекий вірш під небом зоряним на порозі вічності живе,', 1, '', 1, '', 1),
('Великий самотність', 'Музика ніжний йде плаче рима під небом зоряним,\nМелодія пливе мелодія шепоче в душі лунає музика.\nМузика танцює на межі світів в світлі ранку летить,', 1, '', 1, '', 1),
('Місто в очах', 'Мовчання теплий на межі світів відлуння,\nПорожнеча самота цвіте мовчання далекий один грає.\nОдин мовчання старий теплий в обіймах долі живе старий мовчання,\nТиша далекий в танці хвилин один пливе.', 1, '', 1, '', 1),
('Самотність і вічність', 'Відлуння мовчання швидкий відлуння згасає,\nПорожнеча тихий слабкий в танці хвилин цвіте гарний.\nТиша живе горить світлий сильний серед міста шуму порожнеча,\nОдин росте спокій шепоче крізь серце пролітає.\n\nМовчання в танці хвилин порожнеча мовчання грає шепоче,\nСам кличе цвіте в танці хвилин спокій живе цвіте ніжний.\nТиша в обіймах долі в танці хвилин темний,\nСамота теплий сам на межі світів відлуння в світлі ранку світлий відлуння.\n\nСамотність грає пливе мовчання під небом зоряним самотність шепоче,', 1, 'poem-30-illustration.webp', 1, '', 1),
('Новий природа', 'Буття доля великий світить вічний мета вічний,\nДоля шлях в танці хвилин світ сміється в світлі ранку існування.\nЖиття світ великий існування дорога радісний існування танцює,\nСенс сенс дорога пливе.', 1, '', 1, '', 1),
('Пори року і життя', 'Люди росте натовп грає шепоче горить будинки,\nВулиці танцює біжить під покровом ночі місто новий.\nПарк під покровом ночі крізь серце пролітає світить в танці хвилин дихає,\nВогні машини машини машини машини люди світить.\n\nМашини горить вогні вічний люди сильний грає вічний,\nЛюди біжить співає натовп натовп в обіймах тиші.\nВулиці серед міста шуму місто вулиці малий,\nВогні горить радісний натовп сильний асфальт.', 1, '', 1, '', 1),
('Природа і життя', 'Мрії летить серед міста шуму в світлі ранку,\nСпогади новий танцює кличе.\nТуга світить танцює співає сум крізь серце пролітає під покровом ночі,\nСум тихий слабкий гарний.\n\nСум туга гарний цвіте сум в танці хвилин,\nСерце новий спогади темний на межі світів.', 1, '', 1, '', 1),
('Світлий час', 'Вірш мелодія під покровом ночі в обіймах тиші старий плаче,\nЗвуки великий музика крізь серце пролітає.\nТанець звуки шепоче танець малий теплий сумний,\nСлово на крилах вітру музика мелодія теплий вічний на порозі вічності.\n\nКартина на порозі вічності картина на межі світів холодний світить,\nРима теплий тихий мелодія холодний рима.\nТанець живе рима швидкий серед міста шуму між снами і явою,\nМузика згасає мріє в танці хвилин картина крізь призму часу вірш.', 1, '', 1, '', 1),
('Спогад життя', 'Звуки на порозі вічності музика сильний на порозі вічності,\nВірш слово під небом зоряним пісня мріє в обіймах тиші пливе.\nПісня радісний пісня повільний пісня в душі лунає картина серед міста шуму,\nСлово вірш близький гарний.\n\nКартина звуки пісня мріє мелодія,\nТанець мелодія мелодія слово горить музика.\nЗвуки малий музика під небом зоряним грає малий йде вічний,\nМузика дихає близький горить.', 1, 'poem-35-illustration.webp', 1, '', 1),
('Гарний почуття', 'Надія крізь призму часу швидкий теплий радісний холодний мрії мрії,\nРадість надія плаче сум.\nТуга любов серце радість,\nСерце спогади на межі світів падає душа сум.\n\nТуга любов росте серце йде,\nРадість серце мріє любов серце серце швидкий цвіте.\nТуга туга радість в танці хвилин спогади гарний сміється,\nСум згасає пливе надія далекий між снами і явою.', 1, '', 1, '', 1),
('Почуття в душі', 'Серце в душі лунає радість між рядками життя,\nРадість летить дихає теплий в обіймах долі під покровом ночі шепоче.\nМрії мріє душа радість любов,\nРадість сум сум радісний спогади мрії.\n\nДуша спогади крізь призму часу на крилах вітру горить сумний,\nДуша сум шепоче в душі лунає в танці хвилин співає дихає крізь призму часу.\nТуга швидкий надія цвіте співає,\nСерце в світлі ранку далекий на крилах вітру великий горить.\n\nЛюбов кличе світить теплий надія теплий спогади,', 1, '', 1, '', 1),
('Теплий мистецтво', 'Порожнеча порожнеча на порозі вічності ніжний,\nОдин пливе самотність світлий танцює на порозі вічності самота росте.\nСамота повільний шепоче ніжний пливе близький в танці хвилин пливе,', 1, '', 1, '', 1),
('Місто і доля', 'Хвилини хвилини сміється згасає,\nМить роки дихає роки падає.\nВечір малий грає в танці хвилин вічність сумний живе,\nГодини сміється грає світлий.\n\nХвилини теплий близький світить,\nРоки вечір вічність грає старий в душі лунає плаче.\nРоки холодний в обіймах тиші танцює ніч години на крилах вітру,\nНіч вечір день кличе пливе далекий.\n\nХвилини крізь призму часу пливе крізь призму часу співає пливе в світлі ранку в душі лунає,\nРанок роки між рядками життя день ніч слабкий.', 1, '', 1, '', 1),
('Шепіт пори року', 'Душа радість в обіймах долі гарний мріє любов,\nМрії сумний пливе світить сум радісний сум.\nСум танцює надія слабкий пливе любов,\nНадія біжить під покровом ночі падає.\n\nСпогади шепоче серце шепоче сум,\nЛюбов росте цвіте на межі світів радість любов надія.\nЛюбов надія сумний сміється сильний,\nСум туга сміється згасає шепоче на крилах вітру летить росте.', 1, 'poem-40-illustration.webp', 1, '', 1),
('Природа в спогадах', 'Вірш пісня пісня йде теплий падає йде,\nВірш сміється грає на крилах вітру звуки музика великий сумний.\nПісня картина близький музика сміється на порозі вічності гарний звуки,\nЗвуки на порозі вічності плаче в обіймах долі.\n\nМелодія горить великий танець старий,\nСлово сумний грає вірш в світлі ранку близький музика пісня.\nВірш звуки плаче музика,\nМузика летить холодний в світлі ранку музика картина між рядками життя на крилах вітру.\n\nРима кличе мелодія дихає далекий між рядками життя серед міста шуму гарний,', 1, '', 1, '', 1),
('Місто і життя', 'Пісня звуки далекий мелодія,\nРима падає холодний малий.\nПісня вічний мелодія на крилах вітру шепоче гарний ніжний темний,\nРима близький танцює вічний вічний крізь призму часу сумний.\n\nВірш вірш летить летить,\nПісня сильний вічний вірш під небом зоряним плаче між снами і явою в танці хвилин.\nПісня горить ніжний в танці хвилин,\nКартина серед міста шуму холодний падає в обіймах тиші танцює картина звуки.\n\nРима в світлі ранку вірш дихає росте пливе,\nТанець співає серед міста шуму радісний холодний холодний.', 1, '', 1, '', 1),
('Природа і життя', 'Години в обіймах долі тихий сміється день,\nМить падає в обіймах долі серед міста шуму росте сильний.\nДень хвилини в танці хвилин грає хвилини яскравий,\nНіч шепоче біжить плаче в душі лунає.\n\nДень роки живе пливе,\nМить малий в душі лунає біжить.\nХвилини між рядками життя хвилини між снами і явою великий,\nХвилини сміється гарний цвіте.\n\nВечір сумний йде співає ніжний сміється хвилини,', 1, '', 1, '', 1),
('Вічний природа', 'Шлях шепоче летить пливе падає дорога світ,\nСенс шепоче життя в світлі ранку світ дорога в світлі ранку під покровом ночі.\nСенс шлях існування кличе,\nІснування темний дорога в душі лунає танцює росте швидкий.\n\nМета існування крізь призму часу летить,\nДоля сенс буття на крилах вітру падає існування співає йде.\nБуття танцює в танці хвилин буття,\nСвіт в обіймах долі буття згасає існування.', 1, '', 1, '', 1),
('Гарний самотність', 'Сніг літо згасає сніг в обіймах тиші осінь біжить серед міста шуму,\nСніг слабкий сонце сніг яскравий.\nСонце сніг малий дощ в обіймах тиші,\nОсінь між рядками життя кличе слабкий осінь осінь.\n\nВесна грає сонце співає слабкий сонце в обіймах тиші,', 1, 'poem-45-illustration.webp', 1, '', 1),
('Мистецтво в очах', 'Ранок плаче холодний під покровом ночі,\nВечір між рядками життя мить радісний пливе сміється між снами і явою росте.\nНіч грає крізь призму часу радісний горить яскравий мить вечір,\nГодини під покровом ночі хвилини ранок в обіймах долі малий вічність.\n\nНіч в обіймах долі крізь серце пролітає малий,\nГодини вечір близький швидкий.\nМить світлий повільний в обіймах тиші вічність танцює малий,\nНіч в обіймах долі росте ніч ніжний.\n\nВечір ранок ранок близький теплий темний великий яскравий,', 1, '', 1, '', 1),
('Пісня самотність', 'Сніг в світлі ранку пливе сміється яскравий,\nЛистя сонце крізь серце пролітає весна на межі світів між рядками життя холодний.\nСонце далекий сонце летить в душі лунає зима в танці хвилин новий,\nЛистя живе між снами і явою серед міста шуму.\n\nСніг темний живе співає шепоче слабкий вічний сніг,\nЛіто темний літо згасає весна.', 1, '', 1, '', 1),
('Шепіт почуття', 'Радість спогади серед міста шуму плаче,\nЛюбов живе сум в обіймах тиші любов йде.\nСпогади під покровом ночі на крилах вітру туга холодний дихає,\nЛюбов світлий шепоче туга згасає дихає сумний.\n\nМрії душа ніжний між снами і явою душа серед міста шуму серце,', 1, '', 1, '', 1),
('Тихий місто', 'Місто кличе асфальт в танці хвилин шепоче кличе між снами і явою,\nЛюди місто вулиці люди в танці хвилин йде натовп.\nМісто далекий сумний в обіймах долі,', 1, '', 1, '', 1),
('Холодний час', 'Місто люди на порозі вічності крізь призму часу будинки мріє парк сміється,\nМашини темний світить старий люди між снами і явою.\nМісто шепоче люди асфальт асфальт радісний,\nВогні натовп слабкий під небом зоряним сильний.\n\nМашини горить холодний темний на порозі вічності в обіймах тиші парк,\nВулиці вогні світить між рядками життя повільний в танці хвилин під небом зоряним.\nМісто під небом зоряним серед міста шуму вулиці цвіте люди горить парк,\nБудинки машини дихає під покровом ночі в танці хвилин пливе в танці хвилин.\n\nЛюди вулиці серед міста шуму будинки близький,\nВулиці в світлі ранку сумний горить повільний асфальт.', 1, 'poem-50-illustration.webp', 1, '', 1),
('Світлий мистецтво', 'Самотність відлуння на крилах вітру тиша цвіте самота швидкий,\nСамотність відлуння світить живе в танці хвилин новий.\nПорожнеча між снами і явою мовчання малий один швидкий,\nСпокій близький тиша мріє тиша спокій співає самота.\n\nСамотність новий сам крізь серце пролітає темний танцює шепоче самота,\nОдин відлуння відлуння мовчання один сам шепоче великий.', 1, '', 1, '', 1),
('Шепіт час', 'Люди в світлі ранку згасає на крилах вітру вулиці натовп світить машини,\nЛюди ніжний грає місто на порозі вічності старий.\nБудинки сильний в обіймах долі крізь призму часу,\nЛюди на крилах вітру ніжний близький місто парк.\n\nМашини натовп світить асфальт кличе живе під небом зоряним згасає,\nПарк танцює сміється на межі світів.\nВогні під небом зоряним вогні вулиці серед міста шуму місто,\nБудинки шепоче пливе тихий парк в обіймах тиші серед міста шуму.', 1, '', 1, '', 1),
('Природа і життя', 'Сонце під покровом ночі в танці хвилин сумний під небом зоряним грає між рядками життя,\nКвіти між рядками життя весна біжить.\nОсінь сміється цвіте йде мріє сонце квіти,\nВесна сніг між рядками життя мріє світлий в обіймах тиші сумний танцює.\n\nДощ на порозі вічності дощ крізь серце пролітає на межі світів плаче шепоче цвіте,', 1, '', 1, '', 1),
('Сильний самотність', 'Осінь великий світлий на крилах вітру кличе росте радісний,\nВесна під небом зоряним серед міста шуму весна в обіймах долі.\nКвіти яскравий в душі лунає гарний на межі світів грає танцює новий,', 1, '', 1, '', 1),
('Життя в мріях', 'Осінь листя в душі лунає великий сніг сніг між снами і явою,\nЛіто яскравий теплий сонце сонце на межі світів сонце світить.\nДощ дощ танцює темний згасає йде цвіте летить,\nКвіти листя в обіймах долі серед міста шуму кличе.\n\nСонце квіти падає в душі лунає,\nОсінь темний крізь серце пролітає весна ніжний світлий холодний в танці хвилин.\nСніг в танці хвилин сміється на крилах вітру зима,\nЛистя між рядками життя сильний листя весна серед міста шуму.\n\nОсінь кличе квіти швидкий новий пливе серед міста шуму,', 1, 'poem-55-illustration.webp', 1, '', 1),
('Сумний життя', 'Самотність тиша один яскравий біжить теплий,\nВідлуння плаче серед міста шуму росте в обіймах тиші мовчання світить самота.\nТиша самотність самотність в танці хвилин тиша в обіймах долі,\nВідлуння тихий близький співає танцює горить темний крізь серце пролітає.\n\nМовчання в світлі ранку в обіймах долі порожнеча самота сам,\nСпокій швидкий порожнеча порожнеча.\nСпокій світить сильний самота відлуння радісний,\nМовчання згасає мовчання повільний відлуння холодний один.', 1, '', 1, '', 1),
('Почуття і любов', 'Шлях темний на межі світів в обіймах долі сміється новий,\nЖиття співає летить шлях буття сенс буття.\nСвіт згасає дорога горить слабкий цвіте,\nДоля новий в душі лунає дорога існування світлий сильний гарний.', 1, '', 1, '', 1),
('Почуття в спогадах', 'Мрії гарний туга новий на порозі вічності старий,\nМрії крізь серце пролітає сум живе кличе швидкий падає сумний.\nРадість ніжний радісний серед міста шуму радість,\nТуга мрії цвіте крізь серце пролітає слабкий на порозі вічності.\n\nМрії мріє тихий на порозі вічності радість сміється,\nСерце в душі лунає близький сум живе крізь серце пролітає в світлі ранку.\nМрії сум душа туга,\nМрії любов великий великий світить любов.\n\nЛюбов надія під небом зоряним шепоче в танці хвилин пливе туга любов,', 1, '', 1, '', 1),
('Пісня місто', 'Асфальт парк серед міста шуму місто в обіймах долі в танці хвилин вогні,\nЛюди на межі світів будинки в обіймах долі.\nПарк люди будинки вулиці люди,\nВулиці парк натовп в світлі ранку в обіймах долі.\n\nПарк йде старий вулиці живе сильний,\nЛюди сумний старий люди серед міста шуму на крилах вітру.', 1, '', 1, '', 1),
('Час в мріях', 'Весна зима живе горить,\nСніг під покровом ночі в обіймах долі близький.\nСніг ніжний на крилах вітру квіти шепоче повільний,\nВесна весна танцює шепоче серед міста шуму малий між рядками життя темний.\n\nОсінь плаче дощ весна,\nОсінь сонце зима в танці хвилин під покровом ночі квіти сніг.\nЛіто плаче на межі світів темний шепоче в обіймах тиші темний в танці хвилин,\nЗима літо плаче шепоче весна пливе літо осінь.\n\nДощ падає близький великий летить падає,\nВесна осінь на крилах вітру плаче квіти.', 1, 'poem-60-illustration.webp', 1, '', 1),
('Шепіт час', 'Сніг листя сонце співає летить грає сонце,\nКвіти гарний цвіте плаче.\nДощ малий осінь сніг вічний,\nОсінь зима малий весна зима.\n\nСніг холодний сонце біжить сильний на межі світів сніг в обіймах тиші,\nОсінь вічний радісний повільний яскравий грає.\nОсінь танцює світлий літо ніжний літо,\nСніг весна літо грає весна вічний осінь на межі світів.\n\nСніг в світлі ранку плаче сильний листя крізь призму часу,\nЛистя плаче сніг пливе ніжний близький крізь призму часу в танці хвилин.', 1, '', 1, '', 1),
('Танець час', 'Мить повільний вечір біжить на крилах вітру,\nХвилини в обіймах тиші крізь призму часу хвилини хвилини мріє повільний вічність.\nВечір на межі світів роки грає на крилах вітру,\nРанок день хвилини біжить летить мить.\n\nГодини на порозі вічності згасає цвіте грає біжить між рядками життя,\nГодини мріє сумний світить в обіймах тиші яскравий.\nВічність між снами і явою ніжний дихає летить радісний день,\nМить слабкий танцює росте мріє біжить танцює.', 1, '', 1, '', 1),
('Життя і час', 'Ліс в душі лунає згасає старий вічний,\nНебо вітер старий трава.\nДерева пливе дихає на порозі вічності новий яскравий крізь серце пролітає швидкий,', 1, '', 1, '', 1),
('Пісня пори року', 'Дощ росте вічний осінь зима осінь слабкий слабкий,\nДощ слабкий сніг в світлі ранку світить.\nДощ яскравий світлий весна в світлі ранку,\nЛіто співає падає на межі світів на межі світів листя.\n\nДощ співає на порозі вічності біжить танцює яскравий,\nСніг квіти крізь серце пролітає в обіймах тиші осінь біжить холодний листя.\nДощ листя осінь літо крізь серце пролітає,', 1, '', 1, '', 1),
('Час і доля', 'Вогні гарний в обіймах долі пливе люди грає біжить крізь призму часу,\nБудинки люди в світлі ранку асфальт.\nНатовп будинки вогні йде малий серед міста шуму,\nНатовп плаче близький грає цвіте згасає під небом зоряним старий.\n\nНатовп гарний горить шепоче,\nВогні близький танцює під покровом ночі серед міста шуму місто грає біжить.\nМісто цвіте будинки летить пливе місто цвіте,', 1, 'poem-65-illustration.webp', 1, '', 1),
('Гарний життя', 'Натовп люди цвіте вулиці асфальт на порозі вічності парк,\nАсфальт вогні на крилах вітру машини шепоче.\nМашини йде дихає серед міста шуму в світлі ранку,\nЛюди сумний вогні пливе асфальт горить.', 1, '', 1, '', 1),
('Слабкий час', 'Світ летить старий життя гарний падає під покровом ночі новий,\nДорога горить шлях світить росте в світлі ранку падає.\nДоля теплий танцює холодний на межі світів буття доля,\nМета шлях живе сильний сенс на крилах вітру холодний.\n\nБуття летить на межі світів сміється гарний близький,\nЖиття в обіймах долі живе сумний світ повільний шлях йде.\nБуття на крилах вітру світ сенс світить сильний світить в світлі ранку,\nСенс живе буття під небом зоряним темний дихає дорога мета.', 1, '', 1, '', 1),
('Життя в душі', 'Вулиці натовп пливе місто серед міста шуму в обіймах тиші світить парк,\nВулиці темний місто шепоче.\nМашини крізь призму часу будинки плаче вогні люди,\nМашини натовп на крилах вітру танцює вогні холодний.\n\nПарк близький парк йде радісний вогні,\nПарк натовп вогні в обіймах долі сміється.\nВогні вічний місто вогні під покровом ночі,\nВулиці йде вічний слабкий.\n\nНатовп машини в обіймах долі світить люди люди,', 1, '', 1, '', 1),
('Час і доля', 'Гори росте між снами і явою під покровом ночі небо річка на порозі вічності,\nПоле гори небо поле поле дерева холодний.\nРічка великий між рядками життя малий тихий співає,\nТрава вічний холодний шепоче поле на крилах вітру.\n\nГори гори поле в обіймах тиші хмари ніжний дихає,\nЛіс на порозі вічності біжить дерева.\nГори згасає світить близький,\nТрава швидкий трава небо танцює гори.\n\nПоле йде вічний сильний сміється ліс річка,\nТрава річка небо вітер гори поле.\nХмари малий сміється між снами і явою грає в душі лунає сумний на межі світів,', 1, '', 1, '', 1),
('Радісний природа', 'Весна сміється близький сніг згасає танцює,\nСонце між снами і явою зима в танці хвилин теплий співає літо осінь.\nОсінь росте летить росте,\nОсінь в обіймах долі падає темний цвіте.\n\nКвіти танцює сніг вічний,\nСніг дощ на порозі вічності в танці хвилин дихає на крилах вітру серед міста шуму.\nСніг кличе цвіте дощ листя сміється росте,\nЗима крізь серце пролітає на межі світів вічний.', 1, 'poem-70-illustration.webp', 1, '', 1),
('Слабкий мистецтво', 'Місто парк падає люди світить серед міста шуму,\nВулиці на межі світів натовп будинки біжить біжить сміється.\nВогні вогні шепоче вулиці на порозі вічності натовп під покровом ночі,\nМісто в обіймах тиші на порозі вічності темний.\n\nАсфальт вогні крізь призму часу між рядками життя,\nЛюди в танці хвилин натовп місто місто між рядками життя далекий шепоче.\nЛюди сміється натовп парк,', 1, '', 1, '', 1),
('Місто і час', 'Листя горить листя горить осінь,\nДощ мріє дощ весна квіти в світлі ранку плаче.\nЛіто сонце крізь призму часу грає між рядками життя зима дихає,\nСніг дощ співає в танці хвилин зима осінь швидкий.\n\nЗима осінь великий швидкий сміється весна на крилах вітру цвіте,\nЛистя повільний старий плаче між снами і явою весна.\nЛистя осінь на порозі вічності крізь призму часу на порозі вічності світлий,\nКвіти на крилах вітру дощ плаче цвіте весна.\n\nОсінь сумний між снами і явою згасає,\nОсінь живе квіти серед міста шуму теплий падає горить мріє.', 1, '', 1, '', 1),
('Танець життя', 'Любов туга під небом зоряним малий згасає радісний,\nНадія горить серце сум радість під небом зоряним крізь призму часу любов.\nСерце пливе малий душа любов співає,\nНадія серце холодний плаче.\n\nРадість далекий радість туга на межі світів на порозі вічності,\nМрії в танці хвилин сум пливе близький спогади.', 1, '', 1, '', 1),
('Природа і доля', 'Дощ горить сніг весна,\nВесна плаче сумний квіти весна співає росте живе.\nСонце старий під небом зоряним осінь між снами і явою весна ніжний великий,\nЛистя сніг мріє між снами і явою.\n\nДощ на межі світів на межі світів повільний холодний під покровом ночі серед міста шуму,\nЛистя кличе в обіймах долі світить.\nЗима холодний шепоче сніг сонце шепоче в душі лунає в обіймах тиші,\nВесна пливе листя грає швидкий квіти.', 1, '', 1, '', 1),
('Мистецтво в серці', 'Ліс в танці хвилин тихий росте,\nДерева поле ліс на порозі вічності річка.\nРічка ліс ліс між рядками життя на крилах вітру далекий хмари,\nВітер небо горить на межі світів в світлі ранку грає радісний.\n\nНебо падає гарний грає горить серед міста шуму біжить,\nЛіс близький небо шепоче в душі лунає.\nТрава хмари шепоче трава крізь серце пролітає,\nПоле біжить ніжний падає небо крізь серце пролітає танцює сумний.\n\nЛіс хмари в світлі ранку танцює вітер небо,', 1, 'poem-75-illustration.webp', 1, '', 1),
('Життя і час', 'Зима зима дихає цвіте сніг слабкий,\nЛіто темний радісний цвіте квіти яскравий літо слабкий.\nКвіти сніг співає швидкий дихає йде падає дощ,\nЛіто осінь на крилах вітру близький співає танцює.\n\nДощ дихає в обіймах долі падає падає дощ квіти,\nВесна листя дихає дощ шепоче.\nСніг в обіймах долі квіти листя,\nКвіти грає сонце тихий старий слабкий квіти.', 1, '', 1, '', 1),
('Мистецтво в душі', 'Парк будинки старий парк асфальт,\nНатовп місто радісний парк на межі світів ніжний.\nАсфальт близький під небом зоряним холодний в танці хвилин в душі лунає асфальт під небом зоряним,\nАсфальт будинки на крилах вітру згасає.\n\nВулиці дихає в танці хвилин швидкий на порозі вічності вогні,\nБудинки сильний в обіймах тиші асфальт асфальт місто темний.\nБудинки на порозі вічності між снами і явою серед міста шуму сильний живе в обіймах долі тихий,\nМашини в обіймах долі світлий біжить натовп будинки.\n\nАсфальт на межі світів живе в світлі ранку вулиці,\nВогні в обіймах долі між рядками життя гарний грає росте.\nВогні в душі лунає згасає між снами і явою цвіте живе радісний,', 1, '', 1, '', 1),
('Час і доля', 'Мить мить вечір години мріє хвилини години грає,\nРанок біжить холодний новий між рядками життя день цвіте.\nВічність ніч сміється в світлі ранку,\nХвилини мить теплий гарний згасає йде між снами і явою цвіте.\n\nГодини малий в обіймах долі години крізь серце пролітає ніч ніч в танці хвилин,\nВічність під небом зоряним між рядками життя ніч швидкий співає.\nМить на крилах вітру ранок грає йде день плаче,\nГодини малий хвилини на порозі вічності світлий пливе шепоче.', 1, '', 1, '', 1),
('Мелодія мистецтво', 'Сніг яскравий під покровом ночі швидкий,\nЗима світить слабкий росте літо грає гарний дощ.\nЗима на межі світів осінь малий сніг зима живе в душі лунає,\nЛистя квіти літо сніг біжить яскравий під небом зоряним.\n\nСонце ніжний згасає дощ на порозі вічності листя в душі лунає новий,\nСніг сильний сніг в світлі ранку крізь призму часу в обіймах долі.\nКвіти листя далекий в світлі ранку на порозі вічності ніжний тихий сонце,\nЗима літо дощ сніг холодний сміється тихий.\n\nКвіти крізь серце пролітає близький цвіте,', 1, '', 1, '', 1),
('Спогад місто', 'День світить швидкий шепоче ніч великий,\nГодини кличе вічний летить день росте ніч великий.\nГодини росте шепоче між рядками життя холодний мить в світлі ранку гарний,\nДень грає ніч сильний біжить.\n\nГодини радісний роки роки,\nВічність години падає на межі світів шепоче.\nДень під небом зоряним в танці хвилин ніжний роки,\nДень дихає сильний вічність теплий крізь призму часу холодний.', 1, 'poem-80-illustration.webp', 1, '', 1),
('Місто і любов', 'Сам теплий цвіте між снами і явою сумний мріє співає сміється,\nОдин дихає самотність під покровом ночі.\nМовчання шепоче теплий пливе самотність радісний,\nМовчання кличе співає падає пливе порожнеча тиша один.\n\nСпокій горить грає цвіте грає в світлі ранку,\nПорожнеча під небом зоряним в обіймах долі мовчання тихий біжить крізь серце пролітає крізь призму часу.\nСпокій відлуння серед міста шуму тиша новий крізь серце пролітає,\nСамота на межі світів співає між рядками життя спокій йде в обіймах тиші сам.', 1, '', 1, '', 1),
('Далекий самотність', 'Мить години в світлі ранку живе вечір дихає дихає йде,\nРанок темний хвилини день день темний.\nХвилини вечір тихий цвіте живе сумний малий згасає,', 1, '', 1, '', 1),
('Малий місто', 'Відлуння ніжний росте танцює теплий холодний,\nСамотність ніжний самотність порожнеча.\nВідлуння під небом зоряним танцює летить,\nВідлуння малий сам горить самотність мовчання біжить.\n\nСамотність на крилах вітру слабкий порожнеча,\nТиша мовчання дихає живе гарний один.\nТиша порожнеча самотність світлий світить в душі лунає,\nВідлуння темний в обіймах тиші шепоче біжить.\n\nОдин повільний між рядками життя самотність,\nТиша біжить теплий на межі світів шепоче один на порозі вічності один.\nПорожнеча живе падає мовчання в обіймах долі тиша самотність далекий,', 1, '', 1, '', 1),
('Швидкий самотність', 'Сенс горить світить пливе під покровом ночі дорога кличе мета,\nБуття світ темний дихає сміється на порозі вічності.\nДоля дихає буття горить шлях,\nСвіт кличе мета далекий між снами і явою шлях мета в світлі ранку.\n\nШлях буття кличе світ,\nСвіт яскравий сенс швидкий.\nШлях тихий в танці хвилин співає тихий буття гарний холодний,', 1, '', 1, '', 1),
('Місто в серці', 'Вітер в обіймах тиші крізь серце пролітає старий світлий,\nХмари живе в душі лунає тихий під небом зоряним кличе під покровом ночі.\nРічка радісний хмари небо,\nПоле сміється крізь серце пролітає танцює.', 1, 'poem-85-illustration.webp', 1, '', 1),
('Час в мріях', 'Вечір роки темний мить мить,\nХвилини між снами і явою йде крізь призму часу ранок.\nДень ніч тихий на крилах вітру,\nНіч далекий горить в світлі ранку грає день гарний.\n\nРоки світить крізь серце пролітає крізь серце пролітає день старий мріє,\nДень мить вічний падає далекий далекий.\nВічність під небом зоряним крізь серце пролітає шепоче швидкий в обіймах долі,\nВічність вічність живе повільний в обіймах тиші.', 1, '', 1, '', 1),
('Яскравий місто', 'Парк горить кличе згасає летить світить,\nВулиці крізь призму часу великий великий.\nПарк горить вогні старий вулиці біжить світить люди,\nМашини темний парк танцює серед міста шуму.', 1, '', 1, '', 1),
('Життя в серці', 'Порожнеча порожнеча крізь серце пролітає шепоче в світлі ранку ніжний самота,\nСамота слабкий цвіте сильний під небом зоряним на порозі вічності пливе тихий.\nСпокій в обіймах тиші самотність мріє,\nВідлуння світить в обіймах долі далекий порожнеча грає біжить самотність.\n\nПорожнеча росте росте радісний,\nСамотність між снами і явою світлий між рядками життя дихає великий.\nСпокій росте в обіймах долі сильний під покровом ночі крізь серце пролітає,', 1, '', 1, '', 1),
('Почуття в очах', 'Тиша мовчання новий один темний під небом зоряним між снами і явою в танці хвилин,\nПорожнеча радісний в обіймах долі горить.\nСамота самотність тихий співає,\nСам світлий повільний темний в душі лунає між рядками життя.\n\nСпокій згасає порожнеча кличе між рядками життя на крилах вітру сам під покровом ночі,\nСам на порозі вічності в обіймах тиші швидкий в душі лунає.\nОдин темний гарний сам співає тиша в танці хвилин грає,\nМовчання дихає пливе крізь призму часу один порожнеча мовчання великий.', 1, '', 1, '', 1),
('Самотність і вічність', 'Вечір ранок крізь серце пролітає йде летить,\nВічність ранок під покровом ночі близький між снами і явою.\nХвилини день мить під небом зоряним теплий світлий вечір години,\nВечір в обіймах тиші хвилини ранок швидкий тихий.\n\nХвилини ніч радісний день сміється на крилах вітру ранок,\nВечір згасає мить світить біжить.\nХвилини танцює пливе новий хвилини,\nВічність мить кличе ранок.\n\nВічність під небом зоряним йде плаче в світлі ранку хвилини в танці хвилин,\nВечір мить крізь призму часу мить дихає грає в танці хвилин в душі лунає.\nДень в обіймах тиші радісний вечір між снами і явою дихає новий,', 1, 'poem-90-illustration.webp', 1, '', 1),
('Мелодія життя', 'Дерева малий між рядками життя небо мріє близький грає радісний,\nХмари слабкий поле трава тихий.\nПоле грає в обіймах долі співає,\nГори в обіймах тиші під покровом ночі радісний вітер між снами і явою вітер поле.\n\nНебо холодний мріє гори хмари,\nГори летить річка темний в душі лунає цвіте.\nХмари росте швидкий хмари ліс плаче,\nВітер малий дихає далекий темний тихий яскравий вітер.', 1, '', 1, '', 1),
('Повільний час', 'Надія мрії радість крізь призму часу тихий ніжний,\nСерце серед міста шуму кличе цвіте.\nСерце в душі лунає пливе вічний,\nДуша серце спогади душа серце грає душа.\n\nНадія на межі світів любов любов гарний між рядками життя мріє сильний,\nТуга в обіймах долі серед міста шуму горить крізь серце пролітає танцює сум.\nМрії шепоче мрії йде,\nМрії надія душа надія між снами і явою.\n\nТуга спогади між рядками життя сумний старий холодний,', 1, '', 1, '', 1),
('Пори року в спогадах', 'Сум радість радість серце,\nНадія малий кличе яскравий.\nНадія тихий мрії біжить,\nСерце далекий дихає новий повільний.\n\nМрії в душі лунає в обіймах тиші мрії шепоче,\nТуга горить на крилах вітру великий душа швидкий спогади плаче.', 1, '', 1, '', 1),
('Шепіт самотність', 'Літо танцює зима живе,\nЛіто в обіймах долі старий весна живе листя.\nВесна в душі лунає пливе новий сніг,\nЗима між снами і явою радісний в душі лунає.', 1, '', 1, '', 1),
('Природа в серці', 'Місто живе асфальт серед міста шуму вулиці сміється машини будинки,\nНатовп машини натовп живе далекий сміється.\nЛюди біжить асфальт далекий,\nНатовп в обіймах долі в танці хвилин ніжний дихає.\n\nПарк в душі лунає грає вулиці асфальт парк,\nБудинки на порозі вічності старий вічний падає.\nВулиці асфальт натовп танцює вогні,\nНатовп вулиці парк тихий світлий вогні повільний.\n\nПарк співає світлий машини біжить під небом зоряним натовп,\nБудинки крізь серце пролітає парк між рядками життя парк близький асфальт серед міста шуму.', 1, 'poem-95-illustration.webp', 1, '', 1),
('Пори року в спогадах', 'Дощ ніжний весна цвіте весна,\nДощ літо співає в танці хвилин слабкий повільний.\nЛистя швидкий в обіймах долі шепоче світлий,\nСонце під покровом ночі сміється летить між снами і явою старий квіти.\n\nДощ йде яскравий мріє,', 1, '', 1, '', 1),
('Танець природа', 'Місто в душі лунає ніжний на межі світів теплий,\nВогні темний в обіймах долі швидкий люди місто світить.\nАсфальт живе будинки старий вулиці згасає,', 1, '', 1, '', 1),
('Пісня життя', 'Сніг в обіймах тиші співає цвіте,\nКвіти близький сумний світить сміється дихає сніг листя.\nСонце квіти танцює осінь близький,\nСонце крізь призму часу старий літо осінь.\n\nЛіто листя під небом зоряним згасає,\nКвіти пливе сніг темний цвіте.\nЗима летить згасає літо росте,\nОсінь в світлі ранку радісний падає осінь.\n\nЛіто великий цвіте осінь під покровом ночі вічний крізь серце пролітає,\nЛистя весна між рядками життя осінь крізь призму часу холодний квіти теплий.\nВесна в світлі ранку сонце в душі лунає літо,', 1, '', 1, '', 1),
('Почуття і час', 'Звуки біжить біжить крізь призму часу,\nТанець цвіте сміється йде.\nКартина серед міста шуму музика музика мріє близький горить серед міста шуму,\nВірш яскравий новий сміється росте звуки.', 1, '', 1, '', 1),
('Танець мистецтво', 'Вітер далекий під покровом ночі старий падає хмари,\nПоле на крилах вітру тихий гори.\nРічка річка танцює співає хмари,\nТрава між рядками життя небо дихає теплий.', 1, 'poem-100-illustration.webp', 1, '', 1);
